function mapLabel(originalLabel, score) {
  var score = parseFloat(score);

  if (score < 0.50) {
    return "miscellaneous";
  }

  var mappedLabel = tagDictionary[originalLabel];

  if (mappedLabel) {
    return mappedLabel;
  }

  return "invalid_tag";
}

