function getProductLineLevelAndPrefix(filterString) {
    var subsections = filterString.split('^');
    var current = parseInt(subsections[0], 10);
    subsections[0] = (current + 1).toString();
    var toReturn = {  level: current, prefixFilter:subsections.join('^')};
    return toReturn;
}

function getProductLineLineage(currentPrefix)
{   
    var SEPARATOR = "^"
    var SEPARATOR_ENCODED = "%5E"  
    var filterLineage = []
    var prefixLineage = []
    var displayLineage = []
    var sections = currentPrefix.split(SEPARATOR_ENCODED)
    var toReturn = {  filterLineage: filterLineage, prefixLineage: prefixLineage, displayLineage: displayLineage };
    if (sections.length === 1) {
        return toReturn
    }        

    for (i = 0; i < sections.length; i++) {
        sections[i] = decodeURIComponent(sections[i])
    }

    for (j = 2; j < sections.length - 1; j++) {
        var level = j + 1;
        var slice = sections.slice(1, level)
        var prefix = j + SEPARATOR + slice.join(SEPARATOR) + SEPARATOR
        var filter = j - 1 + SEPARATOR + slice.join(SEPARATOR) + SEPARATOR
        prefixLineage.push(prefix);
        filterLineage.push(filter);
        displayLineage.push(sections[j])
    }
    return toReturn;
}

