  var transformerFactory = javax.xml.transform.TransformerFactory.newInstance();
  var transformer = transformerFactory.newTransformer();
  var writer = new java.io.StringWriter();
  transformer.transform(new javax.xml.transform.dom.DOMSource(doc), new javax.xml.transform.stream.StreamResult(writer));
  return writer.toString();
}

function getXMLDocument(innerResponse) {
  var ddoc = null;
  try {
    var dbFactory = javax.xml.parsers.DocumentBuilderFactory.newInstance();
    var dBuilder = dbFactory.newDocumentBuilder();
    var inputSource = new org.xml.sax.InputSource(new java.io.StringReader(innerResponse));
    ddoc = dBuilder.parse(inputSource);
    ddoc.getDocumentElement().normalize();
  } catch (e) {
    logger.error(e.toString());
  }
  return ddoc;
}

